mapper.select:
SELECT ddass.id as id, ddass.stat_year as statYear, ddass.channel_costs as channelCosts, ddass.channel_amount as channelAmount, ddass.channel_margin as channelMargin, ddass.distribution_amount as distributionAmount, ddass.distribution_margin as distributionMargin, ddass.sales_budget as salesBudget, ddass.achieve_rate as achieveRate FROM dashboard_depart_annual_sales_stati ddass WHERE 1=1
and ddass.stat_year between #{fiveYearStr}
and #{oneYearStr}

SELECT ddmhr.id as id, ddmhr.stat_month as statMonth, ddmhr.price_channel_code as priceChannelCode, ddmhr.price_channel_name as priceChannelName, ddmhr.product_sort_code as productSortCode, ddmhr.product_sort_name as productSortName, sum(ddmhr.channel_costs) as channelCosts, sum(ddmhr.channel_sales_amount) as channelSalesAmount, ddmhr.channel_sales_yearonyear as channelSalesYearonyear, ddmhr.channel_sales_link_relative as channelSalesLinkRelative, sum(ddmhr.channel_margin) as channelMargin, ddmhr.channel_margin_yearonyear as channelMarginYearonyear, ddmhr.channel_margin_link_relative as channelMarginLinkRelative, ddmhr.distribution_sales_amount as distributionSalesAmount, ddmhr.distribution_margin as distributionMargin, ddmhr.channel_costs_yearonyear as channelCostsYearonyear, ddmhr.channel_costs_link_relative as channelCostsLinkRelative FROM dashboard_dep_monthly_homocyclic_ratio ddmhr where 1=1
and ddmhr.stat_month between #{beginStatDate}
and #{finishStatDate}
and ddmhr.product_sort_code=#{productSortCode}
and ddmhr.price_channel_code=#{priceChannelCode}
group by ddmhr.stat_month
