mapper.select:
SELECT ddass.id as id, ddass.stat_year as statYear, ddass.product_sort_name as productSortName, ddass.channel_costs as channelCosts, ddass.channel_amount as channelAmount, ddass.distribution_amount as distributionAmount, ddass.channel_margin as channelMargin, ddass.distribution_margin as distributionMargin, ddass.sales_budget as salesBudget, ddass.achieve_rate as achieveRate FROM dashboard_depart_annual_sales_stati ddass WHERE 1=1
and ddass.stat_year between #{fiveYearStr}
and #{oneYearStr}

SELECT ddcpsa.id as id, ddcpsa.stat_month as statMonth, ddcpsa.price_channel_code as priceChannelCode, ddcpsa.price_channel_name as priceChannelName, ddcpsa.product_sort_code as productSortCode, ddcpsa.product_sort_name as productSortName, ddcpsa.product_category_code as productCategoryCode, ddcpsa.product_category_name as productCategoryName, ddcpsa.product_property_code as productPropertyCode, ddcpsa.product_property_name as productPropertyName, ddcpsa.channel_costs as channelCosts, ddcpsa.channel_sales_amount as channelSalesAmount, ddcpsa.channel_sales_yearonyear as channelSalesYearonyear, ddcpsa.channel_sales_link_relative as channelSalesLinkRelative, ddcpsa.distribution_sales_amount as distributionSalesAmount, ddcpsa.channel_margin as channelMargin, ddcpsa.channel_margin_yearonyear as channelMarginYearonyear, ddcpsa.channel_margin_link_relative as channelMarginLinkRelative, ddcpsa.distribution_margin as distributionMargin FROM dashboard_dep_cate_proper_sales_amount ddcpsa where 1=1
and ddcpsa.stat_month=#{statMonth}
and ddcpsa.product_sort_code=#{productSortCode}
and ddmhr.price_channel_code=#{priceChannelCode}

SELECT ddpsa.id as id, ddpsa.stat_month as statMonth, ddpsa.product_sort_code as productSortCode, ddpsa.product_sort_name as productSortName, ddpsa.product_property_code as productPropertyCode, ddpsa.product_property_name as productPropertyName, ddpsa.channel_costs as channelCosts, ddpsa.channel_sales_amount as channelSalesAmount, ddpsa.channel_sales_yearonyear as channelSalesYearonyear, ddpsa.channel_sales_link_relative as channelSalesLinkRelative, ddpsa.distribution_sales_amount as distributionSalesAmount, ddpsa.channel_margin as channelMargin, ddpsa.channel_margin_yearonyear as channelMarginYearonyear, ddpsa.channel_margin_link_relative as channelMarginLinkRelative, ddpsa.distribution_margin as distributionMargin FROM dashboard_dep_proper_sales_amount ddpsa where 1=1
and ddpsa.stat_month=#{statMonth}
and ddpsa.product_sort_code=#{productSortCode}
and ddmhr.price_channel_code=#{priceChannelCode}

SELECT ddcsa.id as id, ddcsa.stat_month as statMonth, ddcsa.price_channel_code as priceChannelCode, ddcsa.price_channel_name as priceChannelName, ddcsa.product_sort_code as productSortCode, ddcsa.product_sort_name as productSortName, ddcsa.product_category_code as productCategoryCode, ddcsa.product_category_name as productCategoryName, ddcsa.channel_costs as channelCosts, ddcsa.channel_sales_amount as channelSalesAmount, ddcsa.channel_sales_yearonyear as channelSalesYearonyear, ddcsa.channel_sales_link_relative as channelSalesLinkRelative, ddcsa.distribution_sales_amount as distributionSalesAmount, ddcsa.channel_margin as channelMargin, ddcsa.channel_margin_yearonyear as channelMarginYearonyear, ddcsa.channel_margin_link_relative as channelMarginLinkRelative, ddcsa.distribution_margin as distributionMargin FROM dashboard_dep_cate_sales_amount ddcsa where 1=1
and ddcsa.stat_month=#{statMonth}
and ddcsa.product_sort_code=#{productSortCode}
and ddmhr.price_channel_code=#{priceChannelCode}

SELECT ddmhr.id as id, ddmhr.stat_month as statMonth, ddmhr.price_channel_code as priceChannelCode, ddmhr.price_channel_name as priceChannelName, ddmhr.product_sort_code as productSortCode, ddmhr.product_sort_name as productSortName, ddmhr.channel_costs as channelCosts, ddmhr.channel_sales_amount as channelSalesAmount, ddmhr.channel_sales_yearonyear as channelSalesYearonyear, ddmhr.channel_sales_link_relative as channelSalesLinkRelative, ddmhr.channel_margin as channelMargin, ddmhr.channel_margin_yearonyear as channelMarginYearonyear, ddmhr.channel_margin_link_relative as channelMarginLinkRelative, ddmhr.distribution_sales_amount as distributionSalesAmount, ddmhr.distribution_margin as distributionMargin, ddmhr.channel_costs_yearonyear as channelCostsYearonyear, ddmhr.channel_costs_link_relative as channelCostsLinkRelative FROM dashboard_dep_monthly_homocyclic_ratio ddmhr where 1=1
and ddmhr.stat_month between #{beginStatDate}
and #{finishStatDate}
and ddmhr.product_sort_code=#{productSortCode}
and ddmhr.price_channel_code=#{priceChannelCode}

SELECT
  sum(ddmhr.channel_sales_amount) as channelSalesAmount
FROM
  dashboard_dep_monthly_homocyclic_ratio ddmhr
where 1=1

    and ddmhr.stat_month = #{statMonth}


    and ddmhr.product_sort_code=#{productSortCode}


    and ddmhr.price_channel_code=#{priceChannelCode}

SELECT
  dnm.negative_margin_sku_num as negativeMarginSkuNum
FROM dashboard_negative_margin dnm
where 1=1

    and dnm.year_month = #{statMonth}


    and dnm.product_sort_code=#{productSortCode}


    and dnm.price_channel_code=#{priceChannelCode}

SELECT
  dsr.stockout_rate as stockoutRate
FROM dashboard_stockout_rate dsr
  where 1=1

      and dsr.year_month = #{statMonth}


      and dsr.product_sort_code=#{productSortCode}


      and dsr.price_channel_code=#{priceChannelCode}

SELECT
dra.amt as amt
FROM dashboard_return_amount dra
where 1=1

    and dra.year_month = #{statMonth}


    and dra.product_sort_code=#{productSortCode}


    and dra.price_channel_code=#{priceChannelCode}
