mapper.select:
select sku_code
FROM bi_suggest_replenishment
where is_product_property_name = #{proStatus}

select sku_code
FROM bi_suggest_replenishment
where is_product_property_name = #{proStatus}

select sku_code
FROM bi_suggest_replenishment
where is_product_property_name = #{proStatus} AND out_stock_continuous_days > #{continuousDays}

select sku_code
FROM bi_suggest_replenishment
where is_product_property_name = #{proStatus} AND out_stock_continuous_days > #{continuousDays}

select bsr.advice_orders as adviceOrders, bsr.average_amount as averageAmount, bsr.out_stock_affect_money as outStockAffectMoney, bsr.out_stock_continuous_days as outStockContinuousDays, apsc.arrival_cycle as arrivalCycle, pssuc.need_days as needDays FROM bi_suggest_replenishment bsr LEFT JOIN apply_product_sku_config AS apsc ON CONVERT(bsr.sku_code USING utf8) COLLATE utf8_unicode_ci = apsc.sku_code AND CONVERT(bsr.transport_center_code USING utf8) COLLATE utf8_unicode_ci = apsc.transport_center_code LEFT JOIN product_sku_supply_unit_capacity AS pssuc ON CONVERT(bsr.sku_code USING utf8) COLLATE utf8_unicode_ci = pssuc.product_sku_code
AND CONVERT(#{supplierCode} USING utf8) COLLATE utf8_unicode_ci = pssuc.supply_unit_code
where 1=1
and bsr.transport_center_code=#{transportCenterCode}
and bsr.sku_code=#{skuCode}
