mapper.select:
select basr.sku_code
FROM bi_app_suggest_replenishment basr
where basr.product_property_code = #{proStatus}

select basr.sku_code
FROM bi_app_suggest_replenishment basr
where basr.product_property_code = #{proStatus} AND basr.stockout_days > #{continuousDays}

select bsr.suggested_order_num as adviceOrders, bsr.sales_avg_3_month_num as averageAmount, bsr.out_stock_affects_amount as outStockAffectMoney, bsr.stock_one_month_days as outStockContinuousDays, bsr.available_num as availableNum, bsr.days_turnover as daysTurnover, psc.arrival_cycle as arrivalCycle, pssuc.out_put as outPuts, pssuc.need_days as needDays FROM bi_app_suggest_replenishment_all bsr LEFT JOIN product_sku_config AS psc ON bsr.sku_code = psc.sku_code AND bsr.transport_center_code = psc.transport_center_code LEFT JOIN product_sku_supply_unit_capacity AS pssuc ON bsr.sku_code = pssuc.product_sku_code
AND CONVERT(#{supplierCode} USING utf8) COLLATE utf8_unicode_ci = pssuc.supply_unit_code
LEFT JOIN supplier_rule AS sr ON sr.id = 999 where 1=1
and bsr.transport_center_code=#{transportCenterCode}
and bsr.sku_code=#{skuCode}
order by pssuc.out_put asc
