select bsar.id as id, #{beginInboundTime} as beginInboundTime, #{finishInboundTime} as finishInboundTime, bsar.supplier_code as supplierCode, bsar.supplier_name as supplierName, bsar.category_level_code as categoryLevelCode, bsar.category_level_name as categoryLevelName, bsar.logistics_center_code as transportCenterCode, bsar.pre_inbound_num as preInboundNum, bsar.pre_tax_amount as preTaxAmount, bsar.pra_inbound_num as praInboundNum, bsar.pra_tax_amount as praTaxAmount, bsar.pra_tax_amount_rate as praTaxAmountRate, bsar.responsible_person_name as responsiblePersonName from bi_supplier_arrival_rate bsar where 1=1
