mapper.select:
select

FROM allocation
where
1=1

  AND create_time >=#{createStartTime,jdbcType=TIMESTAMP}


  AND create_time <=#{createEndTime,jdbcType=TIMESTAMP}


  AND allocation_code  LIKE  CONCAT(CONCAT('%',#{allocationCode},'%'))


  AND allocation_status_code = #{allocationStatusCode}


  AND callout_logistics_center_name LIKE CONCAT('%',#{callOutLogisticsCenterName},'%')


  AND callout_logistics_center_code = #{callOutLogisticsCenterCode}


  AND callout_warehouse_name LIKE  CONCAT('%',#{callOutWarehouseName},'%')


  AND callout_warehouse_code = #{callOutWarehouseCode}


  AND outbound_oder_code LIKE  CONCAT(CONCAT('%',#{outboundOderCode},'%'))


  AND callin_logistics_center_name LIKE  CONCAT('%',#{callInLogisticsCenterName},'%')


  AND callin_logistics_center_code =  #{callInLogisticsCenterCode}


  AND callin_warehouse_name LIKE  CONCAT(CONCAT('%',#{callInWarehouseName},'%'))


  AND callin_warehouse_code =  #{callInWarehouseCode}


  AND inbound_oder_code LIKE  CONCAT(CONCAT('%',#{inboundOderCode},'%'))


  AND company_code = #{companyCode}

order by create_time desc
