mapper.select:
select

from outbound_batch where outbound_oder_code = #{outboundOderCode, jdbcType=VARCHAR}
LIMIT #{beginIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

select

from outbound_batch a left join product_sku b on a.sku_code = b.sku_code
where outbound_oder_code = #{outboundOderCode,jdbcType=VARCHAR}

select

from outbound_batch a left join product_sku_checkout b on a.sku_code = b.sku_code
where a.outbound_oder_code = #{outboundOderCode,jdbcType=VARCHAR}
and a.sku_code = #{skuCode,jdbcType=VARCHAR}

  and a.linenum = #{linenum,jdbcType=BIGINT}
