mapper.select:
select

from product_brand_distributor

    1=1

        and brand_name like concat('%',#{brandName, jdbcType=VARCHAR},'%')


        and brand_id = #{brandId, jdbcType=VARCHAR}

    and distributor_id = #{distributorId, jdbcType=VARCHAR}
    and del_flag=0

limit #{beginIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

mapper.update:
update product_brand_distributor


        brand_initials = #{brandInitials, jdbcType=VARCHAR},


        brand_logo = #{brandLogo, jdbcType=VARCHAR},


        brand_sort = #{brandSort, jdbcType=VARCHAR},


        is_show = #{isShow, jdbcType=BIT},


        brand_top = #{brandTop, jdbcType=BIT},


        update_by = #{updateBy,jdbcType=VARCHAR}


where 1=1

    and brand_id = #{brandId, jdbcType=VARCHAR}


    and distributor_id = #{distributorId, jdbcType=VARCHAR}

update product_brand_distributor set del_flag = 1
where 1=1

    and brand_id = #{brandId, jdbcType=VARCHAR}


    and distributor_id = #{distributorId, jdbcType=VARCHAR}

mapper.select:
select

from product_brand_distributor

    1=1

        and distributor_id = #{req.distributorId, jdbcType=VARCHAR}


        and is_show = #{req.isShow}


        and brand_top = #{req.brandTop}


        and del_flag = #{req.delFlag}


order by brand_initials asc
