mapper.select:
select

from product_category_distributor
where category_id = #{categoryId, jdbcType=VARCHAR} and distributor_id = #{distributorId, jdbcType=VARCHAR}

select

from product_category_distributor
where distributor_id = #{distributorId, jdbcType=VARCHAR}

mapper.update:
update product_category_distributor set del_flag = 1
where 1=1

    and category_id = #{categoryId, jdbcType=VARCHAR}


    and distributor_id = #{distributorId, jdbcType=VARCHAR}
