mapper.update:
update product_dictionary set
del_flag = 1
where id = #{id,jdbcType=BIGINT}

mapper.select:
SELECT

FROM
product_dictionary
WHERE del_flag=0

  and create_time>=#{createTimeStart,jdbcType=TIMESTAMP}


  and create_time<=#{createTimeEnd,jdbcType=TIMESTAMP}


  and create_by LIKE CONCAT(CONCAT('%',#{createBy},'%'))


  and update_by LIKE CONCAT(CONCAT('%',#{updateBy},'%'))


  and product_type LIKE CONCAT(CONCAT('%',#{dictionaryType},'%'))


  and product_name LIKE CONCAT(CONCAT('%',#{dictionaryName},'%'))


  and enabled = #{enabled,jdbcType=TINYINT}


  and company_code = #{companyCode,jdbcType=VARCHAR}

order by create_time DESC

SELECT
COUNT(1)
FROM
product_dictionary
WHERE del_flag=0

  AND product_name=#{productName,jdbcType=VARCHAR}


  AND id!=#{id,jdbcType=BIGINT}


  and company_code = #{companyCode,jdbcType=VARCHAR}

SELECT
sinfo.product_dictionary_value as dictionaryDictionaryValue,
sinfo.product_content as dictionaryContent,
sinfo.product_weight as dictionaryWeight,
sinfo.product_dictionary_info_code as dictionaryInfoCode
FROM product_dictionary sdi LEFT JOIN product_dictionary_info sinfo
ON sdi.product_code=sinfo.product_dictionary_code
WHERE sdi.del_flag=0 AND sinfo.del_flag=0 AND sinfo.enabled=0

  AND sdi.product_name=#{dictionaryName,jdbcType=VARCHAR}


  AND sdi.product_code=#{dictionaryCode,jdbcType=VARCHAR}


  AND sdi.product_type=#{dictionaryType,jdbcType=VARCHAR}


  and company_code = #{companyCode,jdbcType=VARCHAR}

order by sinfo.product_weight ASC
