mapper.select:
SELECT object_id as objectId,handle_type as handleType,handle_name as handleName,object_type as objectType, content,create_time as createTime,create_by as createBy FROM product_operation_log WHERE del_flag=0
AND object_type =#{objectType}
AND object_id =#{objectId}
order by create_time desc

SELECT
object_id as objectId,handle_type as handleType,handle_name as  handleName,object_type as objectType,
content,create_time as createTime,create_by as createBy
FROM product_operation_log
WHERE  del_flag=0

  AND id= #{id,jdbcType=BIGINT}


  AND object_id =#{objectId,jdbcType=VARCHAR}


  AND handle_type = #{handleType,jdbcType=TINYINT}


  AND object_type=#{objectType,jdbcType=TINYINT}

order by create_time desc
