SELECT psku.sku_code as skuCode,psku.sku_name as skuName,psku.product_category_code as productCategoryCode, psku.product_category_name as productCategoryName,psku.product_brand_code as productBrandCode,psku.product_brand_name as productBrandName, psku.spec,psku.color_code as colorCode,psku.color_name as colorName,psku.model_number as modelNumber,psku.unit_code as unitCode,psku.unit_name as unitName,psku.product_sort_code as productSortCode,psku.product_sort_name as productSortName,psku.goods_gifts as goodsGifts, pri.price_value as originalTaxPurchasePrice,pri.price_type_code as priceTypeCode,pri.price_type_name as priceTypeName,pri.supplier_code as supplierCode,pri.supplier_name as supplierName, pri.is_default as isDefault FROM product_sku psku ,product_sku_price pri WHERE psku.sku_code=pri.sku_code AND psku.del_flag=0 AND pri.del_flag=0 AND psku.del_flag=0
AND psku.procurement_section_code=#{procurementSectionCode,jdbcType=VARCHAR}
AND psku.sku_code = #{skuCode,jdbcType=VARCHAR}
AND psku.sku_name = #{skuName,jdbcType=VARCHAR},
AND CONCAT(psku.product_property_code, psku.product_property_name) LIKE CONCAT(CONCAT('%',#{productProperty},'%'))
AND CONCAT(psku.product_category_code, psku.product_category_name) LIKE CONCAT(CONCAT('%',#{productCategory},'%'))
AND CONCAT(psku.product_brand_code, psku.product_brand_name) LIKE CONCAT(CONCAT('%',#{productBrand},'%'))
AND pri.price_type_code=#{priceTypeCode,jdbcType=VARCHAR}

SELECT psku.sku_code as skuCode,psku.sku_name as skuName,psku.product_category_code as productCategoryCode, psku.product_category_name as productCategoryName,psku.product_brand_code as productBrandCode, psku.product_brand_name as productBrandName,psku.spec,psku.color_code as colorCode,psku.color_name as colorName, psku.model_number as modelNumber,psr.supplier_code as supplierCode,psr.supplier_name as supplierName, psr.is_default as isDefault,psku.selection_effective_start_time as takeEffectTime, psr.price_value as originalTaxPurchasePrice,psr.price_type_code as priceTypeCode, psr.price_type_name as priceTypeName FROM product_sku psku LEFT JOIN product_sku_price psr ON psku.sku_code=psr.sku_code WHERE psku.del_flag=0 AND psku.sku_code in
#{item}
AND psr.price_type_code=#{priceTypeCode,jdbcType=VARCHAR}
