mapper.update:
    update apply_product_sku_supply_unit
set update_by = #{auditorBy,jdbcType=VARCHAR},
  update_time = #{auditorTime,jdbcType=TIMESTAMP},
  auditor_status = #{auditorStatus,jdbcType=TINYINT}
where form_no = #{formNo,jdbcType=VARCHAR}

update.foreach:
update apply_product_sku_supply_unit
set be_effective = 1
where product_sku_code = #{item.productSkuCode} and apply_code = #{item.applyCode}

mapper.update:
update product_sku_supply_unit
set is_default = #{isDefault}
where product_sku_code in

   #{item}

mapper.select:
SELECT

 FROM product_sku_supply_unit_draft
where product_sku_code=#{skuCode,jdbcType=VARCHAR}

SELECT
FROM product_sku_supply_unit_draft where product_sku_code in
#{skuCode,jdbcType=VARCHAR}

delete.foreach:
delete from product_sku_supply_unit
where product_sku_code = #{item.productSkuCode}
and supply_unit_code = #{item.supplyUnitCode}

mapper.select:
SELECT

from apply_product_sku_supply_unit
WHERE 1=1

    and product_sku_code=#{skuCode,jdbcType=VARCHAR}


    and apply_code=#{applyCode}

select factory_sku_code as factorySkuCode from product_sku_supply_unit
where product_sku_code = #{skuCode,jdbcType=VARCHAR}
and supply_unit_code = #{supplyUnitCode,jdbcType=VARCHAR}

SELECT

from apply_product_sku_supply_unit
where form_no = #{formNo}

select a.id aid, a.supply_unit_code asupply_unit_code, a.supply_unit_name asupply_unit_name, a.no_tax_purchase_price ano_tax_purchase_price, a.tax_included_price atax_included_price, a.joint_franchise_rate ajoint_franchise_rate, a.point apoint, a.tax_rate atax_rate, a.factory_sku_code afactory_sku_code, a.is_default ais_default, a.product_sku_code aproduct_sku_code, a.product_sku_name aproduct_sku_name, a.usage_status ausage_status, a.apply_code aapply_code, a.del_flag adel_flag, a.create_time acreate_time, a.create_by acreate_by, a.update_time aupdate_time, a.update_by aupdate_by, a.categories_supply_channels_code acategories_supply_channels_code, a.categories_supply_channels_name acategories_supply_channels_name, a.auditor_status aauditor_status, a.selection_effective_start_time aselection_effective_start_time, a.selection_effective_time aselection_effective_time, a.apply_code aapply_code, a.form_no aform_no, a.be_effective abe_effective from apply_product_sku_supply_unit a where a.product_sku_code in
#{item}
and a.auditor_status = 1 and a.apply_show = 0

select a.id, b.sku_code, b.sku_name, b.procurement_section_code, b.procurement_section_name, a.supply_unit_code, a.supply_unit_name, a.no_tax_purchase_price, a.tax_included_price, a.joint_franchise_rate, a.point, a.factory_sku_code, a.categories_supply_channels_code, a.categories_supply_channels_name, a.is_default, a.usage_status, a.create_by, a.create_time, a.update_by, a.update_time, CONCAT(lv1.category_name,'-',lv2.category_name,'-',lv3.category_name,'-',lv4.category_name) product_category_name, b.product_category_code, b.product_brand_code, b.product_brand_name, b.product_property_code, b.product_property_name from product_sku_supply_unit a left join product_sku b on a.product_sku_code = b.sku_code LEFT JOIN product_category lv4 on lv4.category_id = b.product_category_code LEFT JOIN product_category lv3 on lv3.category_id = lv4.parent_id LEFT JOIN product_category lv2 on lv2.category_id = lv3.parent_id LEFT JOIN product_category lv1 on lv1.category_id = lv2.parent_id where a.del_flag = 0
and b.sku_code like CONCAT('%',#{skuCode},'%')
and b.sku_name like CONCAT('%',#{skuName},'%')
and a.supply_unit_code like CONCAT('%',#{supplierCode},'%')
and a.supply_unit_name like CONCAT('%',#{supplierName},'%')
and a.tax_included_price >= #{minPurchasePrice} * 100
and a.tax_included_price >= #{maxPurchasePrice} * 100
and a.categories_supply_channels_code = #{categoriesSupplyChannelsCode}
and a.categories_supply_channels_name like CONCAT('%',#{categoriesSupplyChannelsName},'%')
and a.usage_status = #{usageStatus}
and b.company_code = #{companyCode}
and b.procurement_section_code in (select purchase_group_code from purchase_group_buyer where buyer_code =#{personId,jdbcType=VARCHAR})
and lv1.category_id = #{productCategoryLv1Code,jdbcType=VARCHAR}
and lv2.category_id = #{productCategoryLv2Code,jdbcType=VARCHAR}
and lv3.category_id = #{productCategoryLv3Code,jdbcType=VARCHAR}
and lv4.category_id = #{productCategoryLv4Code,jdbcType=VARCHAR}
AND b.product_brand_code = #{productBrandCode,jdbcType=VARCHAR}
AND b.product_brand_name like CONCAT('%',#{productBrandName,jdbcType=VARCHAR},'%')
AND b.procurement_section_code = #{purchaseGroupCode,jdbcType=VARCHAR}
AND b.procurement_section_name like CONCAT('%',#{purchaseGroupName,jdbcType=VARCHAR},'%')
AND b.product_property_code = #{productPropertyCode,jdbcType=VARCHAR}
AND b.product_property_name like CONCAT('%',#{productPropertyName,jdbcType=VARCHAR},'%')
order by create_time desc

select
  supply_unit_code
from product_sku_supply_unit
where del_flag = 0

    and product_sku_code = #{skuCode}


    and supply_unit_code = #{vo.supplyUnitCode}


    and is_default = #{vo.isDefault}


    and joint_franchise_rate = #{vo.jointFranchiseRate}


    and point = #{vo.point}


    and tax_included_price = #{vo.taxIncludedPrice}


    and usage_status = #{vo.usageStatus}


    and factory_sku_code = #{vo.factorySkuCode}


    and categories_supply_channels_code = #{vo.categoriesSupplyChannelsCode}

SELECT

FROM product_sku_supply_unit_draft
where id=#{id}

select distinct apply_code, create_by, create_time, auditor_status, update_by auditor_by, update_time auditor_time, form_no, 4 as approval_type, approval_name from apply_product_sku_supply_unit where company_code = #{companyCode} and apply_show = 0
and create_time>=#{createTimeStart,jdbcType=TIMESTAMP}
and create_time<=#{createTimeEnd,jdbcType=TIMESTAMP}
and auditor_status = #{applyStatus}
and apply_code like CONCAT('%',#{code},'%')
and form_no like CONCAT('%',#{formNo},'%')
and approval_name like CONCAT('%',#{approvalName},'%')
and exists (select 1 from product_sku a where a.procurement_section_code = #{purchaseGroupCode} and a.sku_code=product_sku_code)
and exists (select 1 from product_sku a where a.procurement_section_code in (select purchase_group_code from purchase_group_buyer where buyer_code =#{personId,jdbcType=VARCHAR}) and a.sku_code=product_sku_code)
order by create_time desc
