mapper.select:
select 
from stock_month_batch
where sku_code = #{skuCode,jdbcType=VARCHAR}
and warehouse_code = #{warehouseCode,jdbcType=VARCHAR}
and batch_code = #{batchCode,jdbcType=VARCHAR}
order by synchr_time desc limit 1

mapper.delete:
delete from stock_month_batch
where wms_type = #{wmsType,jdbcType=INTEGER}
and synchr_time != #{synchrTime,jdbcType=BIGINT}

mapper.select:
select w.logistics_center_code as transport_center_code, w.logistics_center_name as transport_center_name, w.warehouse_code as warehouse_code, w.warehouse_name as warehouse_name, a.sku_code as skuCode, b.sku_name as skuName, a.batch_code as batchCode, b.product_category_name as productCategoryName, b.product_brand_name as productBrandName, d.spec as spec, b.color_name as colorName, b.model_number as modelNumber, d.unit_name as unitName, b.product_sort_name as productSortName, b.goods_gifts as goodsGifts, b.product_property_name as productPropertyName, b.product_name as spuName, b.product_code as spuCode, a.batch_code as warehouseBatchNumber, #{changePriceType} as changePriceType, #{companyCode} as companyCode from stock_month_batch a left join warehouse w on a.warehouse_code = w.warehouse_code left join product_sku b on a.sku_code = b.sku_code left join product c on b.spu_code = c.product_code left join product_sku_purchase_info d on a.sku_code = d.product_sku_code where b.procurement_section_code = #{purchaseGroupCode} and b.company_code = #{companyCode}
AND a.sku_code = #{skuCode,jdbcType=VARCHAR}
AND b.sku_name LIKE CONCAT('',#{skuName,jdbcType=VARCHAR},'%')
AND b.product_category_name LIKE CONCAT('',#{productCategoryName,jdbcType=VARCHAR},'%')
AND b.product_brand_name LIKE CONCAT('',#{productBrandName,jdbcType=VARCHAR},'%')
AND b.product_property_code = #{productPropertyCode,jdbcType=VARCHAR}
AND c.product_code = #{spuCode,jdbcType=VARCHAR}
AND c.product_name LIKE CONCAT('',#{spuName,jdbcType=VARCHAR},'%')
LIMIT #{beginIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

select count(1) from stock_month_batch a left join product_sku b on a.sku_code = b.sku_code left join product c on b.spu_code = c.product_code left join product_sku_purchase_info d on a.sku_code = d.product_sku_code where b.procurement_section_code = #{purchaseGroupCode} and b.company_code = #{companyCode}
AND a.sku_code = #{skuCode,jdbcType=VARCHAR}
AND b.sku_name LIKE CONCAT('',#{skuName,jdbcType=VARCHAR},'%')
AND b.product_category_name LIKE CONCAT('',#{productCategoryName,jdbcType=VARCHAR},'%')
AND b.product_brand_name LIKE CONCAT('',#{productBrandName,jdbcType=VARCHAR},'%')
AND b.product_property_code = #{productPropertyCode,jdbcType=VARCHAR}
AND c.product_code = #{spuCode,jdbcType=VARCHAR}
AND c.product_name LIKE CONCAT('',#{spuName,jdbcType=VARCHAR},'%')
