mapper.select:
select

from warhouse_config
where  1=1

     and warehouse_name LIKE concat('%', #{warehouseName,jdbcType=VARCHAR}, '%')


    and  warehouse_code LIKE concat('%', #{warehouseCode,jdbcType=VARCHAR}, '%')


    and    warehouse_type = #{warehouseType,jdbcType=VARCHAR}


    and   stock_code LIKE concat('%',  #{stockCode,jdbcType=VARCHAR}, '%')


    and   stock_name LIKE concat('%',  #{stockName,jdbcType=VARCHAR}, '%')


    and   docking_type = #{dockingType,jdbcType=INTEGER}


    and  docking_system = #{dockingSystem,jdbcType=INTEGER}


    and  counterpart_warehouse_name  LIKE concat('%',  #{counterpartWarehouseName,jdbcType=VARCHAR}, '%')


    and    counterpart_warehouse_code LIKE concat('%',  #{counterpartWarehouseCode,jdbcType=VARCHAR}, '%')


    and   counterpart_stock_code LIKE concat('%',  #{counterpartStockCode,jdbcType=VARCHAR}, '%')


    and   counterpart_stock_name LIKE concat('%',  #{counterpartStockName,jdbcType=VARCHAR}, '%')


    and     enable = #{enable,jdbcType=BIT}


    and  create_by = #{createBy,jdbcType=VARCHAR}


    and   create_time = #{createTime,jdbcType=TIMESTAMP}


    and    update_by = #{updateBy,jdbcType=VARCHAR}


    update_time = #{updateTime,jdbcType=TIMESTAMP}
