mapper.select:
select

from apply_product_sku_config
where apply_code = #{applyCode,jdbcType=VARCHAR}
and sku_code = #{skuCode}

select
  distinct apply_code,
  create_by,
  create_time,
  auditor_status,
  auditor_by,
  auditor_time,
  2 as approval_type
from apply_product_sku_config
where company_code = #{companyCode}

  and create_time>=#{createTimeStart,jdbcType=TIMESTAMP}


  and create_time<=#{createTimeEnd,jdbcType=TIMESTAMP}


  and auditor_status = #{applyStatus}


  and apply_code = #{code}


  and sku_name like CONCAT('%',#{skuName},'%')

mapper.update:
update apply_product_sku_config
set auditor_by = #{auditorBy,jdbcType=VARCHAR},
  auditor_time = #{auditorTime,jdbcType=TIMESTAMP},
  auditor_status = #{auditorStatus,jdbcType=TINYINT}
where form_no = #{formNo,jdbcType=VARCHAR}
