  #{item}

select distinct apply_code, update_by as create_by, update_time as create_time, apply_status, auditor_by, auditor_time, 1 as approval_type from apply_product_sku where company_code = #{companyCode}
and update_time>=#{createTimeStart,jdbcType=TIMESTAMP}
and update_time<=#{createTimeEnd,jdbcType=TIMESTAMP}
and apply_status = #{applyStatus}
and apply_code like CONCAT('%',#{code},'%')
and sku_name like CONCAT('%',#{skuName},'%')
order by update_time desc
