where
apply_status = #{number}
AND sku_code in

  #{item}

select distinct apply_code, update_by as create_by, update_time as create_time, apply_status, auditor_by, auditor_time, form_no, approval_name approvalName, approval_remark approvalRemark, 1 as approval_type, apply_type from apply_product_sku where company_code = #{companyCode}
and update_time>=#{createTimeStart,jdbcType=TIMESTAMP}
and update_time<=#{createTimeEnd,jdbcType=TIMESTAMP}
and apply_status = #{applyStatus}
and apply_type = #{applyType}
and apply_code like CONCAT('%',#{code},'%')
and form_no like CONCAT('%',#{formNo},'%')
and sku_name like CONCAT('%',#{skuName},'%')
and procurement_section_code = #{purchaseGroupCode}
and procurement_section_code in (select purchase_group_code from purchase_group_buyer where buyer_code =#{personId,jdbcType=VARCHAR})
order by update_time desc
