mapper.select:
select distinct apply_code as code, apply_type as applyType, 3 as approvalType, create_by as createBy, create_time as createTime, update_time as updateTime, update_by as updateBy, auditor_by as auditorBy, auditor_time as auditorTime, apply_status as applyStatus, form_no as formNo from apply_product_sku_sale_area_main where company_code = #{companyCode}
and create_time>=#{createTimeStart,jdbcType=TIMESTAMP}
and create_time<=#{createTimeEnd,jdbcType=TIMESTAMP}
and apply_status = #{applyStatus}
and apply_code like CONCAT('%',#{code},'%')
and form_no like CONCAT('%',#{formNo},'%')
order by create_time desc

select

from apply_product_sku_sale_area_main
where apply_status = #{applyStatus} and official_code = #{officialCode}

mapper.update:
update apply_product_sku_sale_area_main set
be_effective = #{beEffective},
auditor_by = #{auditorBy,jdbcType=VARCHAR}, auditor_time = #{auditorTime,jdbcType=TIMESTAMP}, apply_status = #{applyStatus,jdbcType=INTEGER} where form_no = #{formNo}
