mapper.delete:
delete from product where product_code in(

    #{item.productCode,jdbcType=VARCHAR}

)

mapper.select:
SELECT pro.id,pro.product_code as productCode,pro.product_name as productName, COUNT(pku.id) as skuNumber,pro.update_time as updateTime, pro.update_by as updateBy,pro.bar_code as barCode FROM product pro LEFT JOIN product_sku pku on pro.product_code=pku.product_code WHERE pro.del_flag=0
AND pro.create_time>=#{createTimeStart,jdbcType=TIMESTAMP}
AND pro.create_time<=#{createTimeEnd,jdbcType=TIMESTAMP}
AND pro.product_code LIKE CONCAT(CONCAT('%',#{productCode},'%'))
AND pro.product_name LIKE CONCAT(CONCAT('%',#{productName},'%'))
and pro.company_code = #{companyCode,jdbcType=VARCHAR}
GROUP BY pro.product_code ORDER BY pro.create_time DESC

SELECT id,sku_code as skuCode,sku_name as skuName,product_sort_name as productSortName, product_property_name as productPropertyName,product_category_name as productCategoryName, product_brand_name as productBrandName,create_time as createTime,create_by as createBy FROM product_sku psu WHERE psu.del_flag=0
AND product_code=#{productCode,jdbcType=VARCHAR}
AND pro.product_name LIKE CONCAT(CONCAT('%',#{productName},'%'))
