mapper.select:
select  *
from price_apply_promotion
where 1=1

   and apply_promotion_no = #{applyPromotionNo,jdbcType=BIGINT}


   and apply_promotion_name = #{applyPromotionName,jdbcType=VARCHAR}


   and status = #{status,jdbcType=INTEGER}


   and promotion_no = #{promotionNo,jdbcType=BIGINT}


   and promotion_name = #{promotionName,jdbcType=VARCHAR}


  and  create_name = #{createName,jdbcType=VARCHAR}


   and create_timestamp = #{createTimestamp,jdbcType=TIMESTAMP}


   and update_name = #{updateName,jdbcType=VARCHAR}


   and update_timestamp = #{updateTimestamp,jdbcType=TIMESTAMP}


   and operate_name = #{operateName,jdbcType=VARCHAR}


   and operate_timestamp = #{operateTimestamp,jdbcType=TIMESTAMP}


   and procurement_section_code = #{procurementSectionCode,jdbcType=VARCHAR}


   and procurement_section_name = #{procurementSectionName,jdbcType=VARCHAR}


   and categories_supply_channels_code = #{categoriesSupplyChannelsCode,jdbcType=VARCHAR}


   and categories_supply_channels_name = #{categoriesSupplyChannelsName,jdbcType=VARCHAR}


   and remark = #{remark,jdbcType=LONGVARCHAR}
