mapper.select:
select b.id, b.sku_code, b.sku_name, b.use_status, b.transport_center_code, b.transport_center_name, b.warehouse_code, b.warehouse_name, b.warehouse_type, b.create_by_id, b.create_by_name, b.create_time, b.update_by_name, b.update_time, b.update_by_id, ps.product_brand_code, ps.product_brand_name, ps.product_category_code, ps.product_category_name, ps.product_property_code, ps.procurement_section_code, ps.procurement_section_name, ps.product_property_name FROM product_sku_batch b left join product_sku ps on ps.sku_code=b.sku_code WHERE b.use_status=0
and b.sku_code = #{skuCode,jdbcType=VARCHAR}
and b.sku_name LIKE CONCAT('%',#{skuName},'%')
and b.transport_center_code = #{transportCenterCode,jdbcType=VARCHAR},
and b.warehouse_code = #{warehouseCode,jdbcType=VARCHAR}
and ps.product_property_code = #{productPropertyCode,jdbcType=VARCHAR}
and ps.product_category_code = #{productCategoryCode,jdbcType=VARCHAR}
and ps.product_brand_code = #{productBrandCode,jdbcType=TIMESTAMP}
and ps.procurement_section_code = #{purchaseGroupCode,jdbcType=VARCHAR}

select count(1)
from product_sku_batch
where use_status = 0
and sku_code = #{skuCode,jdbcType=VARCHAR}
and warehouse_code = #{warehouseCode,jdbcType=VARCHAR}
