mapper.select:
select
from product_sku where company_code = #{companyCode} and sku_code in
#{item}

select
from product_sku where company_code = #{companyCode} and sku_name in
#{item}

update.foreach:
update product_sku
set sku_status = #{item.skuStatus},
on_sale = #{item.onSale}
where sku_code = #{item.skuCode}
