mapper.select:
select a.id
from product_sku_sale_area a
left join product_sku_sale_area_info b
on a.code = b.main_code
left join product_sku_sale_area_main c
on a.code = c.code
where c.company_code = #{companyCode}

  and a.sku_code = #{skuCode}


  and a.sku_name like CONCAT('%',#{skuName},'%')


  and c.be_disable = #{beDisable}


  and a.categories_supply_channels_code = #{categoriesSupplyChannelsCode}


  and a.direct_delivery_supplier_code like CONCAT('%',#{directDeliverySupplierCode},'%')

group by a.id

mapper.delete:
delete from product_sku_sale_area
where code in

  #{item}
