mapper.select:
select

from profit_loss
where del_flag = 0

  and  create_time >= #{createTimeStart}


  and  create_time <= #{createTimeEnd}


  and order_code LIKE CONCAT('%',#{orderCode},'%')


  and order_type = #{orderType}


  and logistics_center_code = #{logisticsCenterCode}


  and logistics_center_name like CONCAT('%',#{logisticsCenterName},'%')


  and warehouse_code = #{warehouseCode}


  and warehouse_name like CONCAT('%',#{warehouseName},'%')


  and company_code = #{companyCode,jdbcType=VARCHAR}

order by create_time desc

select

from
  profit_loss
where  synchr_time <= #{finishTime,jdbcType=VARCHAR}
and synchr_time >= #{beginTime,jdbcType=VARCHAR}
and synchr_status = 0
