mapper.select:
SELECT

FROM
variable_price
WHERE del_flag=0

    AND price_type_code=#{priceTypeCode,jdbcType=VARCHAR}


    AND variable_price_code=#{variablePriceCode,jdbcType=VARCHAR}


    AND status=#{status,jdbcType=TINYINT}

ORDER BY create_time DESC

SELECT
psk.sku_code as skuCode,
psk.sku_name as skuName,
psp.price_type_code as priceTypeCode,
psp.price_type_name as priceTypeName,
psp.price_value as taxAmount,
str_to_date(psk.selection_effective_start_time,'%Y-%m-%d %H:%i:%s') as takeEffectTime,
str_to_date(psk.selection_effective_end_time,'%Y-%m-%d %H:%i:%s') as failureTime,
psp.supplier_code as supplierCode,
psp.supplier_name as supplierName,
psp.is_default as isDefault,
psp.id as productSkuPriceId
FROM product_sku psk
LEFT JOIN product_sku_price psp
ON psk.sku_code=psp.sku_code
WHERE psk.del_flag=0
AND psp.del_flag=0

    AND psk.procurement_section_code =#{procurementSectionCode,jdbcType=VARCHAR}


    AND psp.price_type_code=#{priceTypeCode,jdbcType=VARCHAR}


    AND psk.sku_code LIKE CONCAT(CONCAT('%',#{skuCode},'%'))


    AND psk.sku_name LIKE CONCAT(CONCAT('%',#{skuName},'%'))


    AND psp.price_value >=#{amountSta,jdbcType=BIGINT}


    AND psp.price_value <=#{amountEnd,jdbcType=BIGINT}


    AND psk.product_category_code= #{productCategoryCode,jdbcType=VARCHAR}


    AND psk.product_brand_code=#{productBrandCode,jdbcType=VARCHAR}


    AND psk.product_property_code =#{productPropertyCode,jdbcType=VARCHAR}


    AND psk.selection_effective_start_time >=#{takeEffectTimeSta,jdbcType=TIMESTAMP}


    AND psk.selection_effective_start_time <=#{takeEffectTimeEnd,jdbcType=TIMESTAMP}


    AND psk.selection_effective_end_time >=#{failureTimeSta,jdbcType=TIMESTAMP}


    AND psk.selection_effective_end_time <=#{failureTimeEnd,jdbcType=TIMESTAMP}


    AND psp.supplier_code =#{supplierCode,jdbcType=VARCHAR}

GROUP BY psp.id

SELECT vpk.sku_code as skuCode, vpri.variable_price_code as variablePriceCode, vpri.variable_price_name as variablePriceName, vpri.create_time as createTime, vpk.take_effect_time as takeEffectTime, vpk.failure_time as failureTime, vpri.price_type_code as priceTypeCode, vpri.price_type_name as priceTypeName, vpri.procurement_section_code as procurementSectionCode, vpri.procurement_section_name as procurementSectionName, vpri.status as status, vpri.update_time as updateTime, vpri.price_revoke as priceRevoke, vpri.update_by as updateBy FROM variable_price vpri LEFT JOIN variable_price_sku vpk ON vpri.variable_price_code=vpk.variable_price_code WHERE vpri.del_flag=0
AND vpk.sku_code LIKE CONCAT(CONCAT('%',#{skuCode},'%'))
AND vpri.variable_price_name LIKE CONCAT(CONCAT('%',#{variablePriceName},'%'))
AND vpri.price_type_code =#{priceTypeCode,jdbcType=VARCHAR}
AND vpri.procurement_section_code=#{procurementSectionCode,jdbcType=VARCHAR}
AND vpri.create_time >=#{createStaTime,jdbcType=TIMESTAMP}
AND vpri.create_time <=#{createEndTime,jdbcType=TIMESTAMP}
AND vpri.status=#{status,jdbcType=TINYINT}
AND vpri.variable_price_code LIKE CONCAT(CONCAT('%',#{variablePriceCode},'%'))

SELECT
FROM variable_price WHERE del_flag=0
AND form_no=#{formNo,jdbcType=VARCHAR}
