mapper.select:
SELECT ps.procurement_section_code as purchaseGroupCode, ps.procurement_section_name as purchaseGroupName from bi_smart_replenishment bs inner join product_sku ps on bs.sku_code = ps.sku_code inner join product_sku_supply_unit su on ps.sku_code = su.product_sku_code and su.supply_unit_code = bs.supplier_code inner join product_sku_purchase_info pi on ps.sku_code = pi.product_sku_code inner join product_sku_config sc on bs.transport_center_code = sc.transport_center_code and bs.sku_code = sc.sku_code left join stock s on s.sku_code = bs.sku_code and s.warehouse_code = bs.warehouse_code where ps.del_flag = 0 and ps.sku_status = 0 and and ps.goods_gifts != 2 and sc.config_status = 0
and bs.create_time >= #{beginTime,jdbcType=VARCHAR}
and bs.create_time <= #{finishTime,jdbcType=VARCHAR}
group by ps.procurement_section_code

SELECT bs.sku_code as skuCode, bs.sku_name as skuName, bs.transport_center_code as transportCenterCode,bs.transport_center_name as transportCenterName, bs.warehouse_code as warehouseCode, bs.warehouse_name as warehouseName, bs.supplier_code as supplierCode, bs.supplier_name as supplierName, s.stock_unit_name as stockUnitName,s.inventory_num as stockCount, s.total_way_num as totalWayNum, s.update_time as lastTime, s.new_purchase_price as newPurchasePrice, ps.box_gauge as boxGauge, ps.product_brand_code as brandId,ps.product_brand_name as brandName, ps.product_category_code as categoryId,ps.product_category_name as categoryName, ps.product_property_code as productPropertyCode, ps.product_property_name as productPropertyName,ps.goods_gifts as productType, ps.product_sort_code as productSortCode, ps.product_sort_name as productSortName, ps.color_name as colorName,ps.model_number as modelNumber, ps.product_code as spuCode, ps.product_name as productName, ps.spec as productSpec,ps.procurement_section_code as purchaseGroupCode, ps.procurement_section_name as purchaseGroupName, pi.base_product_content as baseProductContent, su.factory_sku_code as factorySkuCode from bi_smart_replenishment bs inner join product_sku ps on bs.sku_code = ps.sku_code inner join product_sku_supply_unit su on ps.sku_code = su.product_sku_code and su.supply_unit_code = bs.supplier_code inner join product_sku_purchase_info pi on ps.sku_code = pi.product_sku_code inner join product_sku_config sc on bs.transport_center_code = sc.transport_center_code and bs.sku_code = sc.sku_code left join stock s on s.sku_code = bs.sku_code and s.warehouse_code = bs.warehouse_code where ps.del_flag = 0 and ps.sku_status = 0 and and ps.goods_gifts != 2 and sc.config_status = 0
and bs.create_time >= #{beginTime,jdbcType=VARCHAR}
and bs.create_time <= #{finishTime,jdbcType=VARCHAR}
and ps.procurement_section_code = #{purchaseGroupCode,jdbcType=VARCHAR}
