mapper.select:
select b.order_original_name orderOriginal, b.customer_name customerName, b.customer_code customerCode, a.sku_name skuName, a.sku_code skuCode, a.spec spec, a.unit_name unitName, a.price price, a.amount amount, b.order_code orderCode, b.transport_center_name transportCenterName, b.warehouse_name warehouseName, a.give_promotion givePromotion, b.create_date createDate, '1' as status, b.create_by_name createByName, b.create_time createTime, b.update_by_name updateByName, b.update_time updateTime from order_info_item a left join order_info b on a.order_code = b.order_code where b.company_code = #{companyCode}
and b.create_time >=#{createDateStart,jdbcType=TIMESTAMP}
and b.create_time <=#{createDateEnd,jdbcType=TIMESTAMP}
and b.transport_center_code = #{transportCenterCode}
and b.warehouse_code = #{warehouseCode}
and b.customer_name like concat('%',#{storeName},'%')
and b.customer_code like concat('%',#{storeCode},'%')
and a.sku_name like concat('%',#{skuName},'%')
and a.sku_code like concat('%',#{skuCode},'%')
and b.order_code LIKE CONCAT(CONCAT('%',#{orderCode},'%'))

select b.order_original_name orderOriginal, b.customer_name customerName, b.customer_code customerCode, 1 as uniqueCode, a.sku_name skuName, a.sku_code skuCode, a.spec spec, a.unit_name unitName, a.price price, a.amount amount, b.order_code orderCode, b.transport_center_name transportCenterName, b.warehouse_name warehouseName, a.give_promotion givePromotion, b.create_date createDate, '1' as status, b.create_by_name createByName, b.create_time createTime, b.update_by_name updateByName, b.update_time updateTime from order_info_item a left join order_info b on a.order_code = b.order_code where b.company_code = #{companyCode}
and b.create_time >=#{createDateStart,jdbcType=TIMESTAMP}
and b.create_time <=#{createDateEnd,jdbcType=TIMESTAMP}
and b.transport_center_code = #{transportCenterCode}
and b.warehouse_code = #{warehouseCode}
and b.customer_name like concat('%',#{storeName},'%')
and b.customer_code like concat('%',#{storeCode},'%')
and a.sku_name like concat('%',#{skuName},'%')
and a.sku_code like concat('%',#{skuCode},'%')
and b.order_code LIKE CONCAT(CONCAT('%',#{orderCode},'%'))

