mapper.select:
select order_code orderCode,
       order_type orderType,
       customer_name customerName,
       transport_center_name transportCenterName,
       warehouse_name warehouseName,
       supplier_name supplierName,
       order_status orderStatus,
       create_date createDate,
       product_total_amount productTotalAmount,
       order_amount orderAmount,
       product_num productNum
from order_info
where company_code = #{companyCode} and order_type = #{orderType}

  and create_date >=#{createDateStart,jdbcType=TIMESTAMP}


  and create_date <=#{createDateEnd,jdbcType=TIMESTAMP}


  and order_status = #{orderStatus}


  and transport_center_code = #{transportCenterCode}


  and warehouse_code = #{warehouseCode}


  and supplier_code = #{supplierCode}


  and be_lock = #{beLock}


  and be_exception = #{beException}


  and customer_name like concat('%',#{customerName},'%')


  and order_code = #{orderCode}
