mapper.select:
select order_code orderCode, order_type orderType, customer_name customerName, transport_center_name transportCenterName, warehouse_name warehouseName, supplier_name supplierName, order_status orderStatus, create_date createDate, product_num productNum, product_total_amount productTotalAmount, product_channel_total_amount productChannelTotalAmount, actual_product_num actualProductNum, actual_product_channel_total_amount actualProductChannelTotalAmount, actual_product_total_amount actualProductTotalAmount, order_amount orderAmount, create_by_name createByName, create_time createTime, update_by_name updateByName, update_time updateTime, order_product_type orderProductType from order_info where company_code = #{companyCode}
and order_type_code = #{orderType}
and create_time >=#{createDateStart,jdbcType=TIMESTAMP}
and create_time <=#{createDateEnd,jdbcType=TIMESTAMP}
and order_status = #{orderStatus}
and transport_center_code = #{transportCenterCode}
and warehouse_code = #{warehouseCode}
and supplier_code = #{supplierCode}
and be_lock = #{beLock}
and be_exception = #{beException}
and customer_name like concat('%',#{customerName},'%')
and order_code LIKE CONCAT(CONCAT('%',#{orderCode},'%'))
order by update_time desc

