mapper.select:
select
purchase_apply_id as purchaseApplyId, purchase_apply_code as purchaseApplyCode, apply_type as applyType,
apply_status as applyStatus, purchase_group_code as purchaseGroupCode, purchase_group_name as purchaseGroupName,
create_time as createTime, create_by_id as createById, create_by_name as createByName,
update_by_id as updateById, update_by_name as updateByName, update_time as updateTime
from purchase_apply

  status = 0 and
  purchase_group_code in

    #{item.purchaseGroupCode,jdbcType=VARCHAR}


    and create_time >= #{beginTime,jdbcType=VARCHAR}


    and create_time <= #{finishTime,jdbcType=VARCHAR}


    and purchase_apply_code = #{purchaseApplyCode,jdbcType=VARCHAR}


    and purchase_group_code = #{purchaseGroupCode,jdbcType=VARCHAR}


    and apply_type = #{applyType,jdbcType=INTEGER}


    and apply_status = #{applyStatus,jdbcType=INTEGER}


order by create_time desc
LIMIT #{beginIndex,jdbcType=INTEGER}, #{pageSize,jdbcType=INTEGER}

select count(1) from purchase_apply

  status = 0 and
  purchase_group_code in

    #{item.purchaseGroupCode,jdbcType=VARCHAR}


    and create_time >= #{beginTime,jdbcType=VARCHAR}


    and create_time <= #{finishTime,jdbcType=VARCHAR}


    and purchase_apply_code = #{purchaseApplyCode,jdbcType=VARCHAR}


    and purchase_group_code = #{purchaseGroupCode,jdbcType=VARCHAR}


    and apply_type = #{applyType,jdbcType=INTEGER}


    and apply_status = #{applyStatus,jdbcType=INTEGER}

select 
from purchase_apply
where status = 0 and purchase_apply_id = #{purchaseApplyId,jdbcType=VARCHAR}

mapper.update:
update purchase_apply
set status = 1
where purchase_apply_id = #{purchaseApplyId,jdbcType=VARCHAR}
