select
a.purchase_whole as purchaseWhole, a.purchase_single as purchaseSingle, a.product_type as productType,
a.product_purchase_amount as productPurchaseAmount, a.apply_product_id as applyProductId,
a.purchase_apply_id as purchaseApplyId, a.sku_code as skuCode, a.base_product_content as baseProductContent,
a.product_spec as productSpec, a.color_name as colorName, a.model_number as modelNumber,
a.stock_unit_name as stockUnitName, s.Input_tax_rate as taxRate, a.product_type as productType,
a.brand_id as brandId, a.brand_name as brandName, a.category_id as categoryId, a.category_name as categoryName,
s.settlement_method_code as settlementMethodCode, s.settlement_method_name as settlementMethodName,
a.sku_name as skuName, a.box_gauge as boxGauge, pa.apply_type as applyType
from purchase_apply_product a
inner join product_sku_checkout s on a.sku_code = s.sku_code
inner join purchase_apply pa on pa.purchase_apply_id = a.purchase_apply_id

  a.apply_product_status = 1 and  a.info_status = 0

    and a.supplier_code = #{supplierCode,jdbcType=VARCHAR}


    and a.transport_center_code = #{transportCenterCode,jdbcType=VARCHAR}


    and a.purchase_group_code = #{purchaseGroupCode,jdbcType=VARCHAR}


    and a.warehouse_code = #{warehouseCode,jdbcType=VARCHAR}


    and s.settlement_method_code = #{settlementMethodCode,jdbcType=VARCHAR}


    and a.purchase_apply_id in

