select
  a.purchase_whole as purchaseWhole, a.purchase_single as purchaseSingle, a.product_type as productType,
  a.product_purchase_amount as productPurchaseAmount, a.base_product_content as baseProductContent, a.sku_code as  skuCode,
  (ifnull(a.purchase_whole, 0) * ifnull(a.base_product_content, 0) + ifnull(a.purchase_single, 0)) as singleCount
  from purchase_apply_product a inner join
  product_sku_checkout s on
  a.sku_code = s.sku_code

    a.apply_product_status = 1 and a.info_status = 0
