mapper.select:
select a.return_order_code returnOrderCode, a.return_order_type returnOrderType, a.customer_name customerName, a.transport_center_name transportCenterName, a.warehouse_name warehouseName, a.supplier_name supplierName, a.order_status orderStatus, a.create_date createDate, a.product_num productNum, a.product_total_amount productTotalAmount, a.return_order_amount returnOrderAmount from return_order_info a left join return_order_info_item b on a.return_order_code = b.return_order_code where a.company_code = #{companyCode}
and a.create_date >=#{createDateStart,jdbcType=TIMESTAMP}
and a.create_date <=#{createDateEnd,jdbcType=TIMESTAMP}
and a.order_status = #{orderStatus}
and a.transport_center_code = #{transportCenterCode}
and a.warehouse_code = #{warehouseCode}
and a.be_delete = #{beDelete}
and a.customer_code like concat('%',#{customerCode},'%')
and a.order_code LIKE CONCAT(CONCAT('%',#{orderCode},'%'))
and a.return_order_code LIKE CONCAT(CONCAT('%',#{returnOrderCode},'%'))
and a.order_type_code in
#{item}
order by create_date desc

select
a.return_order_code returnOrderCode,
a.return_order_type returnOrderType,
a.order_code orderCode,
a.order_type orderType,
a.customer_name customerName,
a.transport_center_name transportCenterName,
a.warehouse_name warehouseName,
a.supplier_name supplierName,
a.order_status orderStatus,
a.create_date createDate,
a.product_num productNum,
a.product_total_amount productTotalAmount,
a.return_order_amount returnOrderAmount
from return_order_info a
left join return_order_info_item b
on a.return_order_code = b.return_order_code
where a.company_code = #{companyCode}

  and a.create_date >=#{createDateStart,jdbcType=TIMESTAMP}


  and a.create_date <=#{createDateEnd,jdbcType=TIMESTAMP}


  and a.order_status = #{orderStatus}


  and a.transport_center_code = #{transportCenterCode}


  and a.warehouse_code = #{warehouseCode}


  and a.customer_code like concat('%',#{customerCode},'%')


  and a.order_code = #{orderCode}


  and a.return_order_code = #{returnOrderCode}
