mapper.select:
select sum(ifnull(channel_sales_cost_acc,0)) as channelSalesCost, sum(ifnull(channel_sales_amount_acc,0)) as channelSalesAmount, sum(ifnull(channel_margin_acc,0)) as channelMargin, sum(ifnull(distribution_sales_cost_acc,0)) as distributionSalesCost, sum(ifnull(distribution_sales_amount_acc,0)) as distributionSalesAmount, sum(ifnull(distribution_margin_acc,0)) as distributionMargin, sum(ifnull(channel_amount_acc_last_year,0)) as channelAmountLastYear, sum(ifnull(channel_amount_acc_last_month,0)) as channelAmountLastMonth, sum(ifnull(channel_margin_acc_last_year,0)) as channelMarginLastYear, sum(ifnull(channel_margin_acc_last_month,0)) as channelMarginLastMonth, sum(ifnull(distribution_amount_acc_last_year,0)) as distributionAmountLastYear, sum(ifnull(distribution_amount_acc_last_month,0)) as distributionAmountLastMonth, sum(ifnull(distribution_margin_acc_last_year,0)) as distributionMarginLastYear, sum(ifnull(distribution_margin_acc_last_month,0)) as distributionMarginLastMonth, sum(ifnull(channel_budget_acc,0)) as channelBudget, sum(ifnull(distribution_budget_acc,0)) as distributionBudget from stat_com_month_acc_sales where stat_year = #{year,jdbcType=BIGINT} and stat_month = #{month,jdbcType=BIGINT}
and data_type_code = #{dataTypeCode,jdbcType=BIGINT}
and product_property_code = #{productPropertyCode,jdbcType=BIGINT}

select
product_sort_code as productSortCode, product_sort_name as productSortName
from stat_com_month_acc_sales
where stat_year = #{year,jdbcType=BIGINT}
and stat_month = #{month,jdbcType=BIGINT}

  and data_type_code = #{dataTypeCode,jdbcType=BIGINT}


  and product_property_code = #{productPropertyCode,jdbcType=BIGINT}

group by product_sort_code
order by product_sort_code

select
product_sort_code as productSortCode, product_sort_name as productSortName,
sum(ifnull(channel_sales_cost_acc,0)) as channelSalesCost, sum(ifnull(channel_sales_amount_acc,0)) as channelSalesAmount,
sum(ifnull(channel_margin_acc,0)) as channelMargin, sum(ifnull(distribution_sales_cost_acc,0)) as distributionSalesCost,
