mapper.select:
select sum(ifnull(channel_sales_cost,0)) as channelSalesCost, sum(ifnull(channel_sales_amount,0)) as channelSalesAmount, sum(ifnull(channel_margin,0)) as channelMargin, sum(ifnull(distribution_sales_cost,0)) as distributionSalesCost, sum(ifnull(distribution_sales_amount,0)) as distributionSalesAmount, sum(ifnull(distribution_margin,0)) as distributionMargin, sum(ifnull(channel_amount_last_year,0)) as channelAmountLastYear, sum(ifnull(channel_amount_last_month,0)) as channelAmountLastMonth, sum(ifnull(channel_margin_last_year,0)) as channelMarginLastYear, sum(ifnull(channel_margin_last_month,0)) as channelMarginLastMonth, sum(ifnull(distribution_amount_last_year,0)) as distributionAmountLastYear, sum(ifnull(distribution_amount_last_month,0)) as distributionAmountLastMonth, sum(ifnull(distribution_margin_last_year,0)) as distributionMarginLastYear, sum(ifnull(distribution_margin_last_month,0)) as distributionMarginLastMonth, sum(ifnull(channel_budget,0)) as channelBudget, sum(ifnull(distribution_budget,0)) as distributionBudget from stat_com_sales_yearly where stat_year = #{year,jdbcType=BIGINT}
and data_type_code = #{dataTypeCode,jdbcType=BIGINT}
and product_property_code = #{productPropertyCode,jdbcType=BIGINT}

select
 product_sort_code as productSortCode, product_sort_name as productSortName
from stat_com_sales_yearly
where stat_year = #{year,jdbcType=BIGINT}

  and data_type_code = #{dataTypeCode,jdbcType=BIGINT}


  and product_property_code = #{productPropertyCode,jdbcType=BIGINT}

group by product_sort_code
order by product_sort_code

select product_sort_code as productSortCode, product_sort_name as productSortName, sum(ifnull(channel_sales_cost,0)) as channelSalesCost, sum(ifnull(channel_sales_amount,0)) as channelSalesAmount, sum(ifnull(channel_margin,0)) as channelMargin, sum(ifnull(distribution_sales_cost,0)) as distributionSalesCost, sum(ifnull(distribution_sales_amount,0)) as distributionSalesAmount, sum(ifnull(distribution_margin,0)) as distributionMargin, sum(ifnull(channel_amount_last_year,0)) as channelAmountLastYear, sum(ifnull(channel_amount_last_month,0)) as channelAmountLastMonth, sum(ifnull(channel_margin_last_year,0)) as channelMarginLastYear, sum(ifnull(channel_margin_last_month,0)) as channelMarginLastMonth, sum(ifnull(distribution_amount_last_year,0)) as distributionAmountLastYear, sum(ifnull(distribution_amount_last_month,0)) as distributionAmountLastMonth, sum(ifnull(distribution_margin_last_year,0)) as distributionMarginLastYear, sum(ifnull(distribution_margin_last_month,0)) as distributionMarginLastMonth, sum(ifnull(channel_budget,0)) as channelBudget, sum(ifnull(distribution_budget,0)) as distributionBudget from stat_com_sales_yearly where stat_year = #{year,jdbcType=BIGINT} and product_sort_code = #{productSortCode,jdbcType=VARCHAR}
and data_type_code = #{dataTypeCode,jdbcType=BIGINT}
and product_property_code = #{productPropertyCode,jdbcType=BIGINT}

select
product_sort_code as productSortCode, product_sort_name as productSortName,
price_channel_code as priceChannelCode, price_channel_name as priceChannelName
from stat_com_sales_yearly
where
  stat_year = #{year,jdbcType=BIGINT}
  and product_sort_code = #{productSortCode,jdbcType=VARCHAR}

  and data_type_code = #{dataTypeCode,jdbcType=BIGINT}


  and product_property_code = #{productPropertyCode,jdbcType=BIGINT}

group by price_channel_code
order by price_channel_code

select
product_sort_code as productSortCode, product_sort_name as productSortName,
price_channel_code as priceChannelCode, price_channel_name as priceChannelName,
sum(ifnull(channel_sales_cost,0)) as channelSalesCost, sum(ifnull(channel_sales_amount,0)) as channelSalesAmount,
sum(ifnull(channel_margin,0)) as channelMargin, sum(ifnull(distribution_sales_cost,0)) as distributionSalesCost,
sum(ifnull(distribution_sales_amount,0)) as distributionSalesAmount, sum(ifnull(distribution_margin,0)) as distributionMargin,
sum(ifnull(channel_amount_last_year,0)) as channelAmountLastYear, sum(ifnull(channel_amount_last_month,0)) as channelAmountLastMonth,
sum(ifnull(channel_margin_last_year,0)) as channelMarginLastYear, sum(ifnull(channel_margin_last_month,0)) as channelMarginLastMonth,
sum(ifnull(distribution_amount_last_year,0)) as distributionAmountLastYear, sum(ifnull(distribution_amount_last_month,0)) as distributionAmountLastMonth,
sum(ifnull(distribution_margin_last_year,0)) as distributionMarginLastYear, sum(ifnull(distribution_margin_last_month,0)) as distributionMarginLastMonth
from stat_com_sales_yearly
where stat_year = #{year,jdbcType=BIGINT}
and product_sort_code = #{productSortCode,jdbcType=VARCHAR}
and price_channel_code = #{priceChannelCode,jdbcType=VARCHAR}

  and data_type_code = #{dataTypeCode,jdbcType=BIGINT}


  and product_property_code = #{productPropertyCode,jdbcType=BIGINT}

select product_sort_code as productSortCode, product_sort_name as productSortName, price_channel_code as priceChannelCode, price_channel_name as priceChannelName, store_type_code as storeTypeCode, store_type as storeType, product_property_code as productPropertyCode, product_property_name as productPropertyName, data_type_code as dataTypeCode, data_type as dataType, sum(ifnull(channel_sales_cost,0)) as channelSalesCost, sum(ifnull(channel_sales_amount,0)) as channelSalesAmount, sum(ifnull(channel_margin,0)) as channelMargin, sum(ifnull(distribution_sales_cost,0)) as distributionSalesCost, sum(ifnull(distribution_sales_amount,0)) as distributionSalesAmount, sum(ifnull(distribution_margin,0)) as distributionMargin, sum(ifnull(channel_amount_last_year,0)) as channelAmountLastYear, sum(ifnull(channel_amount_last_month,0)) as channelAmountLastMonth, sum(ifnull(channel_margin_last_year,0)) as channelMarginLastYear, sum(ifnull(channel_margin_last_month,0)) as channelMarginLastMonth, sum(ifnull(distribution_amount_last_year,0)) as distributionAmountLastYear, sum(ifnull(distribution_amount_last_month,0)) as distributionAmountLastMonth,sum(ifnull(distribution_margin_last_year,0)) as distributionMarginLastYear, sum(ifnull(distribution_margin_last_month,0)) as distributionMarginLastMonth, create_time as createTime from stat_com_sales_yearly where stat_year = #{year,jdbcType=BIGINT} and product_sort_code = #{productSortCode,jdbcType=VARCHAR} and price_channel_code = #{priceChannelCode,jdbcType=VARCHAR}
and data_type_code = #{dataTypeCode,jdbcType=BIGINT}
and product_property_code = #{productPropertyCode,jdbcType=BIGINT}
group by store_type_code order by store_type_code
