mapper.select:
select

from apply_contract
WHERE
del_flag = 0 and apply_status = 5

  AND create_time >=#{createStartTime,jdbcType=TIMESTAMP}


  AND create_time <=#{createEndTime,jdbcType=TIMESTAMP}


  AND apply_contract_code  =  #{applyContractCode}


  AND contract_code  =  #{contractCode}


  AND supplier_code = #{supplierCode}


  AND supplier_name LIKE  CONCAT('%',#{supplierName},'%')


  AND purchasing_group_code = #{purchasingGroupCode}


  AND purchasing_group_name LIKE  CONCAT('%',#{purchasingGroupName},'%')


  AND apply_status = #{applyStatus}


  and company_code = #{companyCode,jdbcType=VARCHAR}


  and create_by = #{applyBy,jdbcType=VARCHAR}


  and year_name like concat('%',#{yearName},'%')

order by create_time desc

select a.id, a.apply_type as applyType, a.apply_contract_code as applyCode, a.apply_status as status, '合同' as modelType, '4' as modelTypeCode, a.update_by as applyBy, a.update_time as applyTime, a.auditor_by as approvalBy, a.auditor_time as approvalTime, a.form_no as formNo from apply_contract a where 1=1 and apply_status != 5
and a.create_time >=#{createTimeStart,jdbcType=TIMESTAMP}
and a.create_time <=#{createTimeEnd,jdbcType=TIMESTAMP}
and a.apply_type = #{applyType}
and a.apply_contract_code like concat('%',#{applyCode},'%')
and a.apply_status = #{applyStatus}
and company_code = #{companyCode,jdbcType=VARCHAR}
and update_by = #{applyBy,jdbcType=VARCHAR}
and form_no like concat('%', #{formNo,jdbcType=VARCHAR},'%')
and purchasing_group_code = #{purchaseGroupCode,jdbcType=VARCHAR}
order by create_time desc
