mapper.insert:
insert into logistics_center (id, logistics_center_code, logistics_center_name, 
  enable, del_flag, create_by, 
  create_time, update_by, update_time,company_code,company_name
  )
values (#{id,jdbcType=BIGINT}, #{logisticsCenterCode,jdbcType=VARCHAR}, #{logisticsCenterName,jdbcType=VARCHAR}, 
  #{enable,jdbcType=TINYINT}, #{delFlag,jdbcType=TINYINT}, #{createBy,jdbcType=VARCHAR}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateBy,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}
   #{companyCode,jdbcType=VARCHAR}, #{companyName,jdbcType=VARCHAR}
  )

mapper.select:
SELECT
COUNT(1)
FROM
logistics_center
WHERE del_flag=0

  AND logistics_center_name = #{logisticsCenterName,jdbcType=VARCHAR}


  AND id!=#{id,jdbcType=BIGINT}


  and company_code = #{companyCode,jdbcType=VARCHAR}

SELECT
from logistics_center t where t. enable =1 and t.logistics_center_code in( select a.logistics_center_code from logistics_center_area a where (a.province_code = 100 and a.enable =1 ) or (a.province_code= #{provinceCode,jdbcType=VARCHAR} and a.city_code =99 and a.enable =1 ) or (a.province_code= #{provinceCode,jdbcType=VARCHAR} and a.city_code =#{cityCode,jdbcType=VARCHAR} and a.enable =1 ) )
and company_code = #{companyCode,jdbcType=VARCHAR}
