mapper.update:
update purchase_group
set enable = #{status,jdbcType=TINYINT}
where id = #{id,jdbcType=BIGINT}

mapper.select:
select a.id, a.purchase_group_code as purchaseGroupCode, a.purchase_group_name as purchaseGroupName, a.create_time as createTime, a.create_by as createBy, a.update_time as updateTime, a.update_by as updateBy, a.del_flag as delFlag, a.responsible_person_code as responsiblePersonCode, a.responsible_person_name as responsiblePersonName from purchase_group a LEFT JOIN purchase_group_buyer b ON a.purchase_group_code = b.purchase_group_code where a.enable != 1 and b.enable != 1
and a.company_code = #{companyCode,jdbcType=VARCHAR}
and b.buyer_code = #{personId,jdbcType=VARCHAR}
and a.purchase_group_name like concat('%', #{name,jdbcType=VARCHAR},'%')
order by a.purchase_group_order asc,a.create_time desc

SELECT
COUNT(1)
FROM
purchase_group
WHERE del_flag=0

  AND purchase_group_name = #{purchaseGroupName,jdbcType=VARCHAR}


  AND id!=#{id,jdbcType=BIGINT}


  and company_code = #{companyCode,jdbcType=VARCHAR}

select
from purchase_group where company_code = #{companyCode} and purchase_group_name in
#{item}
