mapper.select:
SELECT a.apply_supplier_code, CASE a.apply_type WHEN 1 THEN '新增申请' WHEN 2 THEN '修改申请' WHEN 3 THEN '逻辑删除申请' ELSE '其他' END apply_type_name, b.supplier_code, a.apply_supplier_name, a.apply_supplier_type, CASE a.apply_status WHEN 1 THEN '审核中' when 2 THEN '审核通过' when 3 THEN '审核未通过' when 4 THEN '已撤销' ELSE '待审' END apply_status_name, a.auditor_by, a.auditor_time, a.create_time, a.create_by, a.update_time, a.update_by FROM apply_supplier a LEFT JOIN supplier b ON a.apply_supplier_code = b.apply_supplier_code WHERE a.del_flag=0
and a.create_time >=#{createTimeStart,jdbcType=TIMESTAMP}
and a.create_time <=#{createTimeEnd,jdbcType=TIMESTAMP}
and a.apply_type =#{applyType,jdbcType=TINYINT}
and a.apply_supplier_code LIKE concat('%',#{applyCode,jdbcType=VARCHAR},'%')
and b.supplier_code LIKE concat('%',#{supplierCode,jdbcType=VARCHAR},'%')
and a.apply_supplier_name LIKE concat('%',#{applySupplierName,jdbcType=VARCHAR},'%')
and a.apply_status = #{reviewerStatus,jdbcType=TINYINT}
and company_code = #{companyCode,jdbcType=VARCHAR}
and create_by = #{applyBy,jdbcType=VARCHAR}
order by a.create_time desc

SELECT COUNT(1) from apply_supplier WHERE del_flag=0

  AND supplier_name =#{name,jdbcType=VARCHAR}


  and supplier_code !=#{code,jdbcType=VARCHAR}


  and company_code = #{companyCode,jdbcType=VARCHAR}
